import React from "react";

function PopupWithForm({ name, title, buttonText, children, isOpen }) {
  return (
    <div className={isOpen ? `popup popup_type_${name} pop` : `popup popup_type_${name}`}>
      <div className="popup__container popup__container_form">
        <form className="popup__form" noValidate>
          <h2 className="popup__title">{title}</h2>
          {children}
          <button
            type="submit"
            className="button button_save popup__input"
          >{buttonText}</button>
        </form>
        <button className="button button_close"></button>
      </div>
    </div>
  );
}

export default PopupWithForm;
