import React from "react";

function Card({ card, onDeleteClick, userId }) {
  //console.log(card);
  const { name, link, likes, _id, owner } = card;
  const isCreatedByCurrentUser = owner._id === _id;
  const isLikedByCurrentUser = likes.some((item) => item._id === userId);

  return (
    <li className="card">
      <img className="card__image" alt={`Pic: ${name}`} src={link} />
      <div className="card__text-container">
        <p className="card__text">{name}</p>
        <div className="card__likes-container">
          {isLikedByCurrentUser ? (
            <button className="button button_like button_like-black" />
          ) : (
            <button className="button button_like" />
          )}
          <p className="card__likes-text">{likes.length}</p>
        </div>
      </div>
      {isCreatedByCurrentUser && (
        <button className="button button_delete" onClick={onDeleteClick} />
      )}
    </li>
  );
}

export default Card;
