import React from "react";
import api from "../utils/"

function Card({ card, onDeleteClick, userId }) {
  //console.log(card);
  const { name, link, likes, _id, owner } = card;
  const isCreatedByCurrentUser = owner._id === _id;

  const [isLikedByCurrentUser, setIsLikedByCurrentUser] = React.useState(
    likes.some((item) => item._id === userId)
  );

  const handleLikeIcon = () => {
    setIsLikedByCurrentUser(!isLikedByCurrentUser);
    let apiMethod = "";
    isLikedByCurrentUser
              ? (apiMethod = "DELETE")
              : (apiMethod = "PUT");
            api
              .changeLikeCardStatus(card.getId(), apiMethod)
              .then((res) => card._renderLikes(res.likes.length))
              .catch((err) => `Error: ${err}`);
  };

  return (
    <li className="card">
      <img className="card__image" alt={`Pic: ${name}`} src={link} />
      <div className="card__text-container">
        <p className="card__text">{name}</p>
        <div className="card__likes-container">
          {isLikedByCurrentUser ? (
            <button
              className="button button_like button_like-black"
              onClick={handleLikeIcon}
            />
          ) : (
            <button className="button button_like" onClick={handleLikeIcon} />
          )}
          <p className="card__likes-text">{likes.length}</p>
        </div>
      </div>
      {isCreatedByCurrentUser && (
        <button className="button button_delete" onClick={onDeleteClick} />
      )}
    </li>
  );
}

export default Card;
