import React from "react";
import editIconLarge from "../images/edit_large_icon.svg";
import api from "../utils/Api";
import Card from "../components/Card";
import { CurrentUserContext } from "../contexts/CurrentUserContext";

function Main({
  onEditProfile,
  onEditAvatar,
  onAddPlace,
  onDeleteClick,
  onCardClick,
}) {
  const currentUser = React.useContext(CurrentUserContext);
  console.log(currentUser);
  //const { name, about, avatar } = currentUser;

  const [cards, setCards] = React.useState({});

  React.useEffect(() => {
    api
      .getInitialCards()
      .then((cardListData) => {
        setCards(cardListData);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  const handleCardLike = (card) => {
    const isLiked = card.likes.some((i) => i._id === currentUser._id);
    api
      .changeLikeCardStatus(card._id, isLiked ? "DELETE" : "PUT")
      .then((newCard) => {
        const newCards = cards.map((c) => (c._id === card._id ? newCard : c));
        setCards(newCards);
      });
  };

  return (
    <main>
      {/* Profile Section  */}
      <section className="profile">
        <div className="profile__avatar-container">
          <img alt="Avatar Pic" className="profile__avatar" src={avatar} />
          <div className="profile__avatar-edit" onClick={onEditAvatar}>
            <img src={editIconLarge} alt="" />
          </div>
        </div>
        <div className="profile__text-container">
          <h1 className="profile__name"></h1>
          <button
            className="button button_edit"
            onClick={onEditProfile}
          ></button>
          <p className="profile__description">{about}</p>
        </div>
        <button className="button button_add" onClick={onAddPlace}></button>
      </section>

      {/* Cards Section  */}
      <section className="elements">
        <ul className="cards">
          {cards.map((card) => (
            <Card
              card={card}
              onDeleteClick={onDeleteClick}
              onCardClick={onCardClick}
              onCardLike={handleCardLike}
              key={card._id}
            />
          ))}
        </ul>
      </section>
    </main>
  );
}

export default Main;
